const yargs = require('yargs');
const notes = require('./notes')
yargs.command({
    command: 'add',
    describe: 'Add a new note',
    builder: {
        title: {
            describe: 'Note title',
            demandOption: true, 
            type: 'string' 
        },
        body: {
            describe: 'Note body',
            demandOption: true,
            type: 'string'
        }
    },
    handler(argv) {
        console.log("Note Added:", argv.title);
        notes.addNote(argv.title, argv.body)
    }
});
yargs.parse();



